import React, { useState, useRef, useEffect } from 'react';
import './styles.css';

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const videoRef = useRef(null);
  const ws = useRef(null);
  const mediaSource = useRef(null);
  const sourceBuffer = useRef(null);
  const queue = useRef([]);
  const reconnectAttempts = useRef(0);

  const connectToStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const videoElement = videoRef.current;
    const status = document.getElementById('status');

    ws.current = new WebSocket('wss://final-5sjc.onrender.com');

    mediaSource.current = new MediaSource();
    videoElement.src = URL.createObjectURL(mediaSource.current);

    ws.current.onopen = () => {
      console.log('WebSocket connected');
      status.textContent = 'Connected to server as viewer';
      reconnectAttempts.current = 0;
      ws.current.send(JSON.stringify({ type: 'viewer', roomId }));
    };

    ws.current.onmessage = (event) => {
      handleWebSocketMessage(event);
    };

    ws.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      status.textContent = 'WebSocket error occurred';
    };

    ws.current.onclose = () => {
      console.warn('WebSocket closed');
      status.textContent = 'Disconnected from server';
      attemptReconnect();
    };

    mediaSource.current.addEventListener('sourceopen', () => {
      initializeSourceBuffer();
    });

    mediaSource.current.addEventListener('error', (error) => {
      console.error('MediaSource error:', error);
      status.textContent = 'MediaSource error occurred';
    });
  };

  const initializeSourceBuffer = () => {
    if (!sourceBuffer.current) {
      try {
        sourceBuffer.current = mediaSource.current.addSourceBuffer('video/webm; codecs=vp8');
        console.log('SourceBuffer initialized');

        sourceBuffer.current.addEventListener('updateend', () => {
          if (queue.current.length > 0 && !sourceBuffer.current.updating) {
            appendBuffer();
          }
        });

        sourceBuffer.current.addEventListener('error', (e) => {
          console.error('SourceBuffer error:', e);
        });
      } catch (error) {
        console.error('Error initializing SourceBuffer:', error);
      }
    }
  };

  const appendBuffer = () => {
    if (queue.current.length > 0 && sourceBuffer.current && !sourceBuffer.current.updating) {
      try {
        const chunk = queue.current.shift();
        sourceBuffer.current.appendBuffer(chunk);
        console.log('Appended chunk to SourceBuffer');
      } catch (error) {
        console.error('Error appending buffer:', error);
      }
    }
  };

  const handleWebSocketMessage = (event) => {
    const status = document.getElementById('status');

    if (typeof event.data === 'string') {
      const message = JSON.parse(event.data);

      if (message.type === 'no-stream') {
        status.textContent = 'No active stream';
      } else if (message.type === 'end-stream') {
        status.textContent = 'Stream ended';
        if (mediaSource.current.readyState === 'open') {
          mediaSource.current.endOfStream();
        }
      } else if (message.type === 'comment') {
        setComments((prevComments) => [
          ...prevComments,
          { viewerId: message.viewerId, message: message.message },
        ]);
      }
    } else if (event.data instanceof Blob || event.data instanceof ArrayBuffer) {
      const reader = new FileReader();
      reader.onload = () => {
        const arrayBuffer = reader.result;
        queue.current.push(new Uint8Array(arrayBuffer));
        appendBuffer();
      };
      reader.readAsArrayBuffer(event.data);
    }
  };

  const attemptReconnect = () => {
    const maxReconnectAttempts = 5;

    if (reconnectAttempts.current < maxReconnectAttempts) {
      reconnectAttempts.current += 1;
      console.log(`Reconnecting attempt ${reconnectAttempts.current}...`);
      setTimeout(connectToStream, 3000); // Retry connection after 3 seconds
    } else {
      console.error('Max reconnect attempts reached');
    }
  };

  const handleSendComment = () => {
    if (newComment.trim()) {
      const commentData = {
        type: 'comment',
        roomId,
        viewerId: 'viewer_' + Math.random().toString(36).substr(2, 9),
        message: newComment,
      };
      ws.current.send(JSON.stringify(commentData));
      setNewComment('');
    }
  };

  useEffect(() => {
    return () => {
      if (ws.current) {
        ws.current.close();
      }
      if (mediaSource.current) {
        mediaSource.current = null;
      }
      if (sourceBuffer.current) {
        sourceBuffer.current = null;
      }
      queue.current = [];
    };
  }, []);

  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <a href="/">Home</a>
            <a href="/about">About</a>
            <a href="/viewer">Streams</a>
            <a href="/contact">Contact</a>
          </div>
        </nav>
      </header>
      <section className="hero">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button onClick={connectToStream}>Connect to Stream</button>
        <video
          id="video"
          ref={videoRef}
          autoPlay
          playsInline
          controls
          style={{ width: '100%' }}
        />
        <p id="status"></p>
      </section>
      <div className="comments-section">
        <h3>Comments</h3>
        <div className="comments-list">
          {comments.map((comment, index) => (
            <div key={index} className="comment">
              <strong>{comment.viewerId}</strong>: {comment.message}
            </div>
          ))}
        </div>
        <input
          type="text"
          placeholder="Type a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <button onClick={handleSendComment}>Send</button>
      </div>
      <footer>
        <div className="footer-content">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
