import React, { useState, useRef, useEffect } from 'react';
import "./styles.css";
import { Link } from "react-router-dom";

const Streamer = () => {
  const [roomId, setRoomId] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');
  const videoRef = useRef(null); // Reference to the video element
  const ws = useRef(null);
  const mediaRecorder = useRef(null);
  const localStream = useRef(null); // Store the active media stream

  const startStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    ws.current = new WebSocket('wss://final-5sjc.onrender.com');

    ws.current.onopen = () => {
      setStatusMessage('Connected to server as streamer');
      ws.current.send(JSON.stringify({ type: 'streamer', roomId }));
      setIsStreaming(true);
    };

    ws.current.onclose = () => {
      setStatusMessage('Disconnected from server');
      setIsStreaming(false);
    };

    ws.current.onerror = (err) => {
      console.error('WebSocket error:', err);
      setStatusMessage('WebSocket error occurred');
      setIsStreaming(false);
    };
  };

  const startSharing = async (getStreamFn, label) => {
    try {
      const mediaStream = await getStreamFn();
      setStatusMessage(`${label} started...`);
      localStream.current = mediaStream;

      // Display local stream in video preview
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      const videoTrack = mediaStream.getVideoTracks()[0];
      mediaRecorder.current = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm; codecs=vp8',
      });

      mediaRecorder.current.ondataavailable = (event) => {
        if (event.data.size > 0 && ws.current?.readyState === WebSocket.OPEN) {
          ws.current.send(event.data);
        }
      };

      mediaRecorder.current.start(100); // Send chunks every 100ms

      videoTrack.onended = () => {
        stopStream();
        setStatusMessage(`${label} ended`);
      };
    } catch (err) {
      console.error(`Error starting ${label.toLowerCase()}:`, err);
      setStatusMessage(`Failed to start ${label.toLowerCase()}`);
    }
  };

  const startScreenSharing = () => {
    startSharing(() => navigator.mediaDevices.getDisplayMedia({ video: true }), 'Screen sharing');
  };

  const startCameraSharing = () => {
    startSharing(() => navigator.mediaDevices.getUserMedia({ video: true }), 'Camera sharing');
  };

  const stopStream = () => {
    mediaRecorder.current?.stop();
    if (localStream.current) {
      localStream.current.getTracks().forEach((track) => track.stop());
      localStream.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsStreaming(false);
    setStatusMessage('Stream stopped');
  };

  useEffect(() => {
    return () => {
      // Cleanup on component unmount
      stopStream();
      ws.current?.close();
    };
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">
          STREAM<span>VIBE</span>
        </div>
        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/">Streams</Link>
          <Link to="/contact">Contact</Link>
        </div>
      </nav>

      {/* Main Section */}
      <section className="hero">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button onClick={startStream}>Start Streaming</button>
        {isStreaming && (
          <div>
            <button onClick={startScreenSharing}>Start Screen Share</button>
            <button onClick={startCameraSharing}>Start Camera Share</button>
            <button onClick={stopStream}>Stop Streaming</button>
          </div>
        )}
        <p>{statusMessage}</p>
      </section>

      {/* Local Preview */}
      <section className="preview-section">
        <h3>Local Preview</h3>
        <video
          ref={videoRef}
          autoPlay
          muted
          style={{
            width: '100%',
            border: '1px solid #ccc',
            borderRadius: '8px',
            marginTop: '10px',
          }}
        ></video>
      </section>

      {/* Footer */}
      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/rules">Rules</Link>
            <Link to="/legacy">Legacy</Link>
            <Link to="/pricing">Pricing</Link>
            <Link to="/contact">Contact</Link>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Streamer;
