import React, { useState, useRef, useEffect } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const [comments, setComments] = useState([]); // Store comments
  const [newComment, setNewComment] = useState(''); // New comment input
  const videoRef = useRef(null);
  const ws = useRef(null);
  const mediaSource = useRef(null);
  const sourceBuffer = useRef(null);
  const queue = useRef([]); // Queue for buffering video chunks if the SourceBuffer is updating

  const connectToStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }
  
    const status = document.getElementById('status');
    const videoElement = videoRef.current;
  
    // Initialize WebSocket
    ws.current = new WebSocket('wss://final-5sjc.onrender.com');
  
    // Initialize MediaSource
    mediaSource.current = new MediaSource();
    videoElement.src = URL.createObjectURL(mediaSource.current);
  
    // WebSocket Handlers
    ws.current.onopen = () => {
      console.log('WebSocket connected');
      status.textContent = 'Connected to server as viewer';
      ws.current.send(JSON.stringify({ type: 'viewer', roomId }));
    };
  
    ws.current.onmessage = (event) => {
      console.log('Received message from WebSocket');
      handleWebSocketMessage(event);
    };
  
    ws.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      status.textContent = 'WebSocket error occurred';
    };
  
    ws.current.onclose = () => {
      console.log('WebSocket closed');
      status.textContent = 'Disconnected from server';
    };
  
    // MediaSource Event Handlers
    mediaSource.current.addEventListener('sourceopen', () => {
      console.log('MediaSource is open');
      status.textContent = 'Ready to stream';
      try {
        sourceBuffer.current = mediaSource.current.addSourceBuffer('video/webm; codecs=vp8');
        console.log('SourceBuffer added');
  
        // Handle appending queued chunks
        sourceBuffer.current.addEventListener('updateend', () => {
          if (queue.current.length > 0 && !sourceBuffer.current.updating) {
            appendChunkToBuffer(queue.current.shift());
          }
        });
      } catch (error) {
        console.error('Error adding SourceBuffer:', error);
        status.textContent = 'Error initializing stream';
      }
    });
  
    mediaSource.current.addEventListener('sourceclose', () => {
      console.log('MediaSource closed');
    });
  
    mediaSource.current.addEventListener('error', (err) => {
      console.error('MediaSource error:', err);
      status.textContent = 'MediaSource error occurred';
    });
  };
  
// Function to handle messages from WebSocket server
const handleWebSocketMessage = (event) => {
  const status = document.getElementById('status');

  if (typeof event.data === 'string') {
    const message = JSON.parse(event.data);

    if (message.type === 'no-stream') {
      status.textContent = 'No active stream';
    } else if (message.type === 'end-stream') {
      status.textContent = 'Stream ended';
      if (mediaSource.current.readyState === 'open') {
        mediaSource.current.endOfStream();
      }
    } else if (message.type === 'comment') {
      setComments((prevComments) => [
        ...prevComments,
        { viewerId: message.viewerId, message: message.message },
      ]);
    }
  } else if (event.data instanceof Blob || event.data instanceof ArrayBuffer) {
    console.log('Received video chunk');
    const reader = new FileReader();
    reader.onload = () => {
      const arrayBuffer = reader.result;
      if (mediaSource.current.readyState === 'open') {
        appendChunkToBuffer(new Uint8Array(arrayBuffer));
      } else {
        console.warn('MediaSource not ready, dropping chunk');
      }
    };
    reader.onerror = (err) => {
      console.error('FileReader error:', err);
      status.textContent = 'Error reading video chunk';
    };
    reader.readAsArrayBuffer(event.data);
  }
};

// Function to append chunks to SourceBuffer or queue them
const appendChunkToBuffer = (chunk) => {
  try {
    if (sourceBuffer.current && !sourceBuffer.current.updating) {
      sourceBuffer.current.appendBuffer(chunk);
      console.log('Appended video chunk');
    } else {
      console.log('Queueing video chunk');
      queue.current.push(chunk);
    }
  } catch (error) {
    console.error('Error appending buffer:', error);
    document.getElementById('status').textContent = 'Error buffering stream';
  }
};

// Update handling for when the SourceBuffer finishes updating
useEffect(() => {
  if (sourceBuffer.current) {
    sourceBuffer.current.addEventListener('updateend', () => {
      console.log('SourceBuffer updateend');
      if (queue.current.length > 0 && !sourceBuffer.current.updating) {
        appendChunkToBuffer(queue.current.shift());
      }
    });
  }

  // Cleanup on unmount
  return () => {
    if (ws.current) {
      ws.current.close();
    }
    if (mediaSource.current) {
      mediaSource.current = null;
    }
    if (sourceBuffer.current) {
      sourceBuffer.current = null;
    }
    queue.current = [];
  };
}, []);


  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/viewer">Streams</Link>
            <Link to="/contact">Contact</Link>
          </div>
        </nav>
      </header>
      <section className="hero">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button onClick={connectToStream}>Connect to Stream</button>
        <video
          id="video"
          ref={videoRef}
          autoPlay
          playsInline
          controls
          style={{ width: '100%' }}
        />
        <p id="status"></p>
      </section>

      {/* Comments Section */}
      <div className="comments-section">
        <h3>Comments</h3>
        <div className="comments-list">
          {comments.map((comment, index) => (
            <div key={index} className="comment">
              <strong>{comment.viewerId}</strong>: {comment.message}
            </div>
          ))}
        </div>

        <input
          type="text"
          placeholder="Type a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        {/*<button onClick={handleSendComment}>Send</button>*/}
      </div>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/rules">Rules</Link>
            <Link to="/legacy">Legacy</Link>
            <Link to="/pricing">Pricing</Link>
            <Link to="/contact">Contact</Link>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
