import React, { useState, useRef, useEffect } from 'react';
import './styles.css';

const Streamer = () => {
  const [roomId, setRoomId] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [comments, setComments] = useState([]); // Store comments
  const [localStream, setLocalStream] = useState(null); // Store local media stream
  const [status, setStatus] = useState(''); // Track WebSocket status
  const ws = useRef(null); // Store WebSocket instance
  const mediaRecorder = useRef(null);
  const videoRef = useRef(null); // Reference for the video element

  // Function to initialize WebSocket and handle reconnection
  const initializeWebSocket = () => {
    if (!ws.current) {
      ws.current = new WebSocket('wss://final-5sjc.onrender.com');
      ws.current.onopen = () => {
        setStatus('Connected to server as streamer');
        if (ws.current) {
          ws.current.send(JSON.stringify({ type: 'streamer', roomId }));
          setIsStreaming(true);
        }
      };

      ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'comment') {
          setComments((prevComments) => [...prevComments, { viewerId: data.viewerId, message: data.message }]);
        }
      };

      ws.current.onclose = () => {
        setStatus('Disconnected from server');
        setIsStreaming(false);
        initializeWebSocket(); // Reattempt to reconnect on close
      };

      ws.current.onerror = (err) => {
        console.error('WebSocket error:', err);
        setStatus('WebSocket error occurred');
        setIsStreaming(false);
        initializeWebSocket(); // Reattempt to reconnect on error
      };
    }
  };

  const startStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    initializeWebSocket(); // Ensure WebSocket is initialized

    // WebSocket will handle streaming after it's open, so no need to directly modify ws here
  };

  const startSharing = async (getStreamFn, label) => {
    const statusElement = document.getElementById('status');
    try {
      const mediaStream = await getStreamFn();
      setStatus(`${label} started...`);
      setLocalStream(mediaStream); // Store the local stream for preview

      // Show local preview
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      const videoTrack = mediaStream.getVideoTracks()[0];
      mediaRecorder.current = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm; codecs=vp8',
      });

      mediaRecorder.current.ondataavailable = (event) => {
        if (event.data.size > 0 && ws.current?.readyState === WebSocket.OPEN) {
          ws.current.send(event.data);
        }
      };

      mediaRecorder.current.start(100); // Send chunks every 100ms

      videoTrack.onended = () => {
        mediaRecorder.current?.stop();
        setStatus(`${label} ended`);
        setLocalStream(null); // Clear the local stream
      };
    } catch (err) {
      console.error(`Error starting ${label.toLowerCase()}:`, err);
      setStatus(`Failed to start ${label.toLowerCase()}`);
    }
  };

  const startScreenSharing = () => {
    startSharing(() => navigator.mediaDevices.getDisplayMedia({ video: true }), 'Screen sharing');
  };

  const startCameraSharing = () => {
    startSharing(() => navigator.mediaDevices.getUserMedia({ video: true }), 'Camera sharing');
  };

  const handleCommentInput = (e) => {
    if (e.key === 'Enter' && e.target.value.trim() !== '') {
      const comment = e.target.value.trim();
      const commentsContainer = document.getElementById('comments');
      const commentElement = document.createElement('p');
      commentElement.textContent = comment;
      commentElement.className = 'comment';
      commentsContainer.appendChild(commentElement);
      e.target.value = ''; // Clear the input
    }
  };

  useEffect(() => {
    return () => {
      // Cleanup on component unmount
      mediaRecorder.current?.stop();
      ws.current?.close();
      if (localStream) {
        localStream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [localStream]);

  return (
    <div>
      <div className="streamer-container">
        <div className="streamer-content">
          <input
            type="text"
            placeholder="Enter Room ID"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <button onClick={startStream}>Start Streaming</button>
          {isStreaming && (
            <div>
              <button onClick={startScreenSharing}>Start Screen Share</button>
              <button onClick={startCameraSharing}>Start Camera Share</button>
            </div>
          )}
        </div>
        <p id="status">{status}</p>

        {isStreaming && (
          <div className="stream-section">
            <div className="video-container">
              <video id="local-stream" className="video-preview" autoPlay muted></video>
            </div>
            <div className="comment-section">
              <h3>Live Comments</h3>
              <div id="comments" className="comments-list"></div>
            </div>
          </div>
        )}
      </div>

      <div>
        <h3>Local Preview:</h3>
        <video ref={videoRef} autoPlay muted style={{ width: '100%', border: '1px solid #ccc' }}></video>
      </div>

      <div>
        <h3>Comments:</h3>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.viewerId}:</strong> {comment.message}
            </li>
          ))}
        </ul>
      </div>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <a href=" ">Home</a>
            <a href="/about">About Us</a>
            <a href="/rules">Rules</a>
            <a href="/legacy">Legacy</a>
            <a href="/pricing">Pricing</a>
            <a href="/contact">Contact</a>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Streamer;
