import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './styles.css';

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const [viewerId, setViewerId] = useState('');
  const [comments, setComments] = useState([]);
  const [status, setStatus] = useState('');
  const [isWatching, setIsWatching] = useState(false);
  const videoRef = useRef(null);
  const ws = useRef(null);

  const initializeWebSocket = () => {
    if (!ws.current) {
      ws.current = new WebSocket('wss://final-5sjc.onrender.com');

      ws.current.onopen = () => {
        console.log('WebSocket connected');
        setStatus('Connected to server as viewer');
        ws.current.send(
          JSON.stringify({ type: 'viewer', roomId, viewerId: viewerId.trim() })
        );
      };

      ws.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'stream') {
          // Handle incoming stream data
          const blob = new Blob([data.stream], { type: 'video/webm' });
          const url = URL.createObjectURL(blob);
          videoRef.current.src = url;
        } else if (data.type === 'comment') {
          setComments((prev) => [
            ...prev,
            { viewerId: data.viewerId, message: data.message },
          ]);
        }
      };

      ws.current.onclose = () => {
        console.warn('WebSocket closed');
        setStatus('Disconnected from server');
        setIsWatching(false);
      };

      ws.current.onerror = (err) => {
        console.error('WebSocket error:', err);
        setStatus('WebSocket error occurred');
      };
    }
  };

  const startWatching = () => {
    if (!roomId.trim() || !viewerId.trim()) {
      alert('Enter both Room ID and Viewer ID!');
      return;
    }
    initializeWebSocket();
    setIsWatching(true);
  };

  const sendComment = (comment) => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      ws.current.send(
        JSON.stringify({ type: 'comment', roomId, viewerId, message: comment })
      );
    }
  };

  useEffect(() => {
    return () => {
      // Cleanup on component unmount
      ws.current?.close();
    };
  }, []);

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          STREAM<span>VIBE</span>
        </div>
        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/">Streams</Link>
          <Link to="/contact">Contact</Link>
        </div>
      </nav>

      <section className="hero">
        <div className="viewer-content">
          <input
            type="text"
            placeholder="Enter Room ID"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Viewer ID"
            value={viewerId}
            onChange={(e) => setViewerId(e.target.value)}
          />
          <button onClick={startWatching}>Join Stream</button>
        </div>

        {isWatching && (
          <div className="stream-section">
            <div className="video-container">
              <video
                ref={videoRef}
                className="video-preview"
                autoPlay
                controls
                style={{ width: '100%', border: '1px solid #ccc' }}
              ></video>
            </div>
            <div className="comment-section">
              <h3>Live Comments</h3>
              <div className="comments-list">
                {comments.map((comment, index) => (
                  <p key={index} className="comment">
                    <strong>{comment.viewerId}:</strong> {comment.message}
                  </p>
                ))}
              </div>
              <input
                type="text"
                placeholder="Type a comment"
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    sendComment(e.target.value);
                    e.target.value = '';
                  }
                }}
              />
            </div>
          </div>
        )}
      </section>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/rules">Rules</Link>
            <Link to="/legacy">Legacy</Link>
            <Link to="/pricing">Pricing</Link>
            <Link to="/contact">Contact</Link>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <Link to="#">Terms of Service</Link>
            <Link to="#">Privacy Policy</Link>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <Link to="#">
                <i className="fab fa-facebook"></i>
              </Link>
              <Link to="#">
                <i className="fab fa-twitter"></i>
              </Link>
              <Link to="#">
                <i className="fab fa-instagram"></i>
              </Link>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
