import React, { useState, useRef } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const videoRef = useRef(null);
  const peerConnection = useRef(null);
  const ws = useRef(null);

  const ICE_SERVERS = {
    iceServers: [
      { urls: 'stun:stun.l.google.com:19302' }, // Public STUN server
    ],
  };

  const connectToStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');
    ws.current = new WebSocket('wss://final-5sjc.onrender.com'); // Update WebSocket URL if needed

    ws.current.onopen = () => {
      console.log('WebSocket connected');
      status.textContent = 'Connected to server as viewer';
      ws.current.send(JSON.stringify({ type: 'viewer', roomId }));
    };

    ws.current.onmessage = async (event) => {
      if (typeof event.data === 'string') {
        try {
          const message = JSON.parse(event.data);

          if (message.type === 'offer') {
            console.log('Received offer');
            await handleOffer(message.offer);
          } else if (message.type === 'ice-candidate') {
            console.log('Received ICE candidate');
            await handleIceCandidate(message.candidate);
          } else if (message.type === 'comment') {
            console.log('Received comment');
            setComments((prev) => [...prev, { viewerId: message.viewerId, message: message.message }]);
          }
        } catch (error) {
          console.error('Error processing message:', error);
        }
      } else {
        console.warn('Unexpected binary data received. Ignoring.');
      }
    };

    ws.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      status.textContent = 'WebSocket error occurred';
    };

    ws.current.onclose = () => {
      console.log('WebSocket closed');
      status.textContent = 'Disconnected from server';
    };
  };

  const handleOffer = async (offer) => {
    console.log('Handling offer');
    peerConnection.current = new RTCPeerConnection(ICE_SERVERS);

    peerConnection.current.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current.send(JSON.stringify({ type: 'ice-candidate', candidate: event.candidate }));
      }
    };

    peerConnection.current.ontrack = (event) => {
      console.log('Track received');
      if (videoRef.current) {
        videoRef.current.srcObject = event.streams[0];
      }
    };

    await peerConnection.current.setRemoteDescription(new RTCSessionDescription(offer));

    const answer = await peerConnection.current.createAnswer();
    await peerConnection.current.setLocalDescription(answer);

    ws.current.send(JSON.stringify({ type: 'answer', answer }));
  };

  const handleIceCandidate = async (candidate) => {
    try {
      await peerConnection.current.addIceCandidate(new RTCIceCandidate(candidate));
      console.log('ICE candidate added');
    } catch (error) {
      console.error('Error adding ICE candidate:', error);
    }
  };

  const handleSendComment = () => {
    if (newComment.trim()) {
      ws.current.send(JSON.stringify({ type: 'comment', roomId, viewerId: 'viewer', message: newComment }));
      setNewComment('');
    }
  };

  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/viewer">Streams</Link>
            <Link to="/contact">Contact</Link>
          </div>
        </nav>
      </header>
      <section className="hero">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button onClick={connectToStream}>Connect to Stream</button>
        <video
          id="video"
          ref={videoRef}
          autoPlay
          playsInline
          controls
          style={{ width: '100%' }}
        />
        <p id="status"></p>
      </section>

      {/* Comments Section */}
      <div className="comments-section">
        <h3>Comments</h3>
        <div className="comments-list">
          {comments.map((comment, index) => (
            <div key={index} className="comment">
              <strong>{comment.viewerId}</strong>: {comment.message}
            </div>
          ))}
        </div>
        <input
          type="text"
          placeholder="Type a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <button onClick={handleSendComment}>Send</button>
      </div>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/rules">Rules</Link>
            <Link to="/legacy">Legacy</Link>
            <Link to="/pricing">Pricing</Link>
            <Link to="/contact">Contact</Link>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
