import React, { useState, useEffect } from 'react';
import "./styles.css";

const Streamer = () => {
  const [roomId, setRoomId] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [localStream, setLocalStream] = useState(null);  // State to store the local stream
  const [statusMessage, setStatusMessage] = useState('');  // State for status messages

  const startStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');
    const ws = new WebSocket('wss://final-5sjc.onrender.com'); // Change to 'wss://' for secure WebSocket

    ws.onopen = () => {
      setStatusMessage('Connected to server as streamer');
      ws.send(JSON.stringify({ type: 'streamer', roomId }));
      setIsStreaming(true);
    };

    ws.onclose = () => {
      setStatusMessage('Disconnected from server');
      setIsStreaming(false);
    };

    ws.onerror = (err) => {
      console.error('WebSocket error:', err);
      setStatusMessage('WebSocket error occurred');
      setIsStreaming(false);
    };
  };

  const startSharing = async (getStreamFn, label) => {
    try {
      const mediaStream = await getStreamFn();
      setLocalStream(mediaStream); // Store the media stream in state
      setStatusMessage(`${label} started...`);

      const mediaRecorder = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm; codecs=vp8',
      });

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0 && ws?.readyState === WebSocket.OPEN) {
          ws.send(event.data);
        }
      };

      mediaRecorder.start(100); // Send chunks every 100ms

      mediaStream.getVideoTracks()[0].onended = () => {
        mediaRecorder.stop();
        setStatusMessage(`${label} ended`);
      };
    } catch (err) {
      console.error(`Error starting ${label.toLowerCase()}:`, err);
      setStatusMessage(`Failed to start ${label.toLowerCase()}`);
    }
  };

  const startScreenSharing = () => {
    startSharing(() => navigator.mediaDevices.getDisplayMedia({ video: true }), 'Screen sharing');
  };

  const startCameraSharing = () => {
    startSharing(() => navigator.mediaDevices.getUserMedia({ video: true }), 'Camera sharing');
  };

  useEffect(() => {
    if (localStream) {
      // Set the local stream on the video element whenever it changes
      const videoElement = document.getElementById('local-video');
      if (videoElement) {
        videoElement.srcObject = localStream;
      }
    }

    // Cleanup the stream on component unmount
    return () => {
      if (localStream) {
        localStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [localStream]);

  return (
    <div>
      <input
        type="text"
        placeholder="Enter Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
      />
      <button onClick={startStream}>Start Streaming</button>
      {isStreaming && (
        <div>
          <button onClick={startScreenSharing}>Start Screen Share</button>
          <button onClick={startCameraSharing}>Start Camera Share</button>
        </div>
      )}
      <p id="status">{statusMessage}</p>

      {/* Local stream preview */}
      {isStreaming && localStream && (
        <div className="local-preview">
          <h3>Local Preview</h3>
          <video
            id="local-video"
            style={{ width: '300px', height: 'auto', border: '2px solid #ddd' }}
            autoPlay
            muted
          />
        </div>
      )}

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <a href=" ">Home</a>
            <a href="/about">About Us</a>
            <a href="/rules">Rules</a>
            <a href="/legacy">Legacy</a>
            <a href="/pricing">Pricing</a>
            <a href="/contact">Contact</a>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Streamer;
