import React, { useState, useRef, useEffect } from 'react';
import "./styles.css";

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const videoRef = useRef(null);
  const ws = useRef(null);
  const mediaSource = useRef(null);

  const connectToStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');
    const videoElement = document.getElementById('video');
    ws.current = new WebSocket('wss://final-5sjc.onrender.com'); // Update to 'wss://' for secure context
    mediaSource.current = new MediaSource();
    videoElement.src = URL.createObjectURL(mediaSource.current);

    ws.current.onopen = () => {
      status.textContent = 'Connected to server as viewer';
      ws.current.send(JSON.stringify({ type: 'viewer', roomId }));
    };

    mediaSource.current.addEventListener('sourceopen', () => {
      const sourceBuffer = mediaSource.current.addSourceBuffer('video/webm; codecs=vp8');

      ws.current.onmessage = (event) => {
        if (typeof event.data === 'string') {
          const message = JSON.parse(event.data);

          if (message.type === 'no-stream') {
            status.textContent = 'No active stream';
          } else if (message.type === 'end-stream') {
            status.textContent = 'Stream ended';
            mediaSource.current.endOfStream();
          }
        } else if (event.data instanceof Blob || event.data instanceof ArrayBuffer) {
          const reader = new FileReader();
          reader.onload = () => {
            const arrayBuffer = reader.result;
            if (!sourceBuffer.updating) {
              sourceBuffer.appendBuffer(new Uint8Array(arrayBuffer));
            }
          };
          reader.readAsArrayBuffer(event.data);
        }
      };

      sourceBuffer.addEventListener('error', (e) => {
        console.error('SourceBuffer Error:', e);
        status.textContent = 'Error in buffering stream';
      });
    });

    ws.current.onerror = () => {
      status.textContent = 'WebSocket error occurred';
    };

    ws.current.onclose = () => {
      status.textContent = 'Disconnected from server';
    };
  };

  useEffect(() => {
    return () => {
      if (ws.current) {
        ws.current.close();
      }
      if (mediaSource.current) {
        mediaSource.current = null;
      }
    };
  }, []);

  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <a href=" ">Home</a>
            <a href="about">About</a>
            <a href="viewer">Streams</a>
            <a href="contact">Contact</a>
          </div>
        </nav>
      </header>

      <input
        type="text"
        placeholder="Enter Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
      />
      <button onClick={connectToStream}>Connect to Stream</button>
      <video id="video" ref={videoRef} autoPlay playsInline />
      <p id="status"></p>
      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <a href="index.html">Home</a>
            <a href="/about.html">About Us</a>
            <a href="/rules.html">Rules</a>
            <a href="/legacy.html">Legacy</a>
            <a href="/pricing.html">Pricing</a>
            <a href="/contact.html">Contact</a>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
