import React, { useState, useRef, useEffect } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';

const Viewer = () => {
  const [roomId, setRoomId] = useState('');
  const [comments, setComments] = useState([]); // Store comments
  const [newComment, setNewComment] = useState(''); // New comment input
  const videoRef = useRef(null);
  const ws = useRef(null); // WebSocket for signaling
  const peerConnection = useRef(null); // WebRTC PeerConnection

  // STUN/TURN server configuration
  const iceServers = {
    iceServers: [
      { urls: 'stun:stun.l.google.com:19302' }, // Google's free STUN server
    ],
  };

  const connectToStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');

    // Initialize WebSocket for signaling
    ws.current = new WebSocket('wss://final-5sjc.onrender.com');
    ws.current.onopen = () => {
      console.log('WebSocket connected');
      status.textContent = 'Connected to signaling server';
      ws.current.send(JSON.stringify({ type: 'viewer', roomId }));
    };

    ws.current.onmessage = async (event) => {
      const message = JSON.parse(event.data);

      if (message.type === 'offer') {
        console.log('Received offer from broadcaster');
        await handleOffer(message.offer);
      } else if (message.type === 'ice-candidate') {
        console.log('Received ICE candidate');
        await handleIceCandidate(message.candidate);
      } else if (message.type === 'comment') {
        console.log('Received comment');
        setComments((prevComments) => [
          ...prevComments,
          { viewerId: message.viewerId, message: message.message },
        ]);
      }
    };

    ws.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      status.textContent = 'WebSocket error occurred';
    };

    ws.current.onclose = () => {
      console.log('WebSocket closed');
      status.textContent = 'Disconnected from signaling server';
    };

    initializePeerConnection();
  };

  const initializePeerConnection = () => {
    peerConnection.current = new RTCPeerConnection(iceServers);

    // When remote stream is added
    peerConnection.current.ontrack = (event) => {
      console.log('Received remote stream');
      videoRef.current.srcObject = event.streams[0];
    };

    // Send ICE candidates to the signaling server
    peerConnection.current.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current.send(
          JSON.stringify({ type: 'ice-candidate', candidate: event.candidate })
        );
        console.log('Sent ICE candidate to signaling server');
      }
    };

    peerConnection.current.onconnectionstatechange = () => {
      const status = document.getElementById('status');
      switch (peerConnection.current.connectionState) {
        case 'connected':
          status.textContent = 'Connected to broadcaster';
          break;
        case 'disconnected':
        case 'failed':
          status.textContent = 'Disconnected from broadcaster';
          break;
        case 'closed':
          status.textContent = 'Connection closed';
          break;
        default:
          break;
      }
    };
  };

  const handleOffer = async (offer) => {
    try {
      await peerConnection.current.setRemoteDescription(
        new RTCSessionDescription(offer)
      );
      const answer = await peerConnection.current.createAnswer();
      await peerConnection.current.setLocalDescription(answer);

      // Send answer back to the broadcaster via WebSocket
      ws.current.send(
        JSON.stringify({ type: 'answer', answer: peerConnection.current.localDescription })
      );
      console.log('Sent answer to broadcaster');
    } catch (error) {
      console.error('Error handling offer:', error);
    }
  };

  const handleIceCandidate = async (candidate) => {
    try {
      await peerConnection.current.addIceCandidate(new RTCIceCandidate(candidate));
      console.log('Added ICE candidate');
    } catch (error) {
      console.error('Error adding ICE candidate:', error);
    }
  };

  // Cleanup on component unmount
  useEffect(() => {
    return () => {
      if (peerConnection.current) {
        peerConnection.current.close();
      }
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/viewer">Streams</Link>
            <Link to="/contact">Contact</Link>
          </div>
        </nav>
      </header>
      <section className="hero">
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <button onClick={connectToStream}>Connect to Stream</button>
        <video
          id="video"
          ref={videoRef}
          autoPlay
          playsInline
          controls
          style={{ width: '100%' }}
        />
        <p id="status"></p>
      </section>

      {/* Comments Section */}
      <div className="comments-section">
        <h3>Comments</h3>
        <div className="comments-list">
          {comments.map((comment, index) => (
            <div key={index} className="comment">
              <strong>{comment.viewerId}</strong>: {comment.message}
            </div>
          ))}
        </div>
        <input
          type="text"
          placeholder="Type a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <button
          onClick={() => {
            if (newComment.trim() && ws.current) {
              ws.current.send(
                JSON.stringify({
                  type: 'comment',
                  roomId,
                  viewerId: `viewer_${Math.random().toString(36).substr(2, 9)}`,
                  message: newComment,
                })
              );
              setNewComment('');
            }
          }}
        >
          Send
        </button>
      </div>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/rules">Rules</Link>
            <Link to="/legacy">Legacy</Link>
            <Link to="/pricing">Pricing</Link>
            <Link to="/contact">Contact</Link>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Viewer;
