import React, { useState, useRef, useEffect } from 'react';

const Streamer = () => {
  const [roomId, setRoomId] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [localStream, setLocalStream] = useState(null);
  const ws = useRef(null);
  const mediaRecorder = useRef(null);
  const videoRef = useRef(null);

  const startStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');
    ws.current = new WebSocket('wss://final-5sjc.onrender.com'); // Change to 'wss://' for secure WebSocket

    ws.current.onopen = () => {
      status.textContent = 'Connected to server as streamer';
      ws.current.send(JSON.stringify({ type: 'streamer', roomId }));
      setIsStreaming(true);
    };

    ws.current.onclose = () => {
      status.textContent = 'Disconnected from server';
      setIsStreaming(false);
    };

    ws.current.onerror = (err) => {
      console.error('WebSocket error:', err);
      status.textContent = 'WebSocket error occurred';
      setIsStreaming(false);
    };
  };

  const startSharing = async (getStreamFn, label) => {
    const status = document.getElementById('status');
    try {
      const mediaStream = await getStreamFn();
      status.textContent = `${label} started...`;
      setLocalStream(mediaStream);

      const videoTrack = mediaStream.getVideoTracks()[0];
      videoRef.current.srcObject = mediaStream; // Set local video stream for preview

      const videoElem = videoRef.current;
      videoElem.play();

      const mediaRecorder = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm; codecs=vp8',
      });

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0 && ws.current?.readyState === WebSocket.OPEN) {
          ws.current.send(event.data);
        }
      };

      mediaRecorder.start(100); // Send chunks every 100ms

      videoTrack.onended = () => {
        mediaRecorder.stop();
        status.textContent = `${label} ended`;
      };
    } catch (err) {
      console.error(`Error starting ${label.toLowerCase()}:`, err);
      status.textContent = `Failed to start ${label.toLowerCase()}`;
    }
  };

  const startScreenSharing = () => {
    startSharing(() => navigator.mediaDevices.getDisplayMedia({ video: true }), 'Screen sharing');
  };

  const startCameraSharing = () => {
    startSharing(() => navigator.mediaDevices.getUserMedia({ video: true }), 'Camera sharing');
  };

  useEffect(() => {
    return () => {
      // Cleanup on component unmount
      mediaRecorder.current?.stop();
      ws.current?.close();
      if (localStream) {
        localStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [localStream]);

  return (
    <div>
      

      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            border: '2px solid #ccc',
            borderRadius: '5px',
            fontSize: '16px',
          }}
        />
        <button onClick={startStream} style={{ padding: '10px 20px', marginLeft: '10px' }}>Start Streaming</button>
      </div>

      {isStreaming && (
        <div>
          <button onClick={startScreenSharing} style={{ padding: '10px 20px', marginTop: '10px' }}>
            Start Screen Share
          </button>
          <button onClick={startCameraSharing} style={{ padding: '10px 20px', marginTop: '10px' }}>
            Start Camera Share
          </button>
        </div>
      )}

      {localStream && (
        <div style={{ marginTop: '20px' }}>
          <h3>Local Preview</h3>
          <video ref={videoRef} style={{ width: '300px', height: 'auto', border: '2px solid #ddd' }} />
        </div>
      )}

      <p id="status" style={{ marginTop: '20px' }}></p>

      <footer style={{ marginTop: '40px', borderTop: '1px solid #ddd', paddingTop: '20px' }}>
        <div>
          <h3>Quick Links</h3>
          <a href="index.html">Home</a> | <a href="/about.html">About Us</a> | <a href="/rules.html">Rules</a> | 
          <a href="/legacy.html">Legacy</a> | <a href="/pricing.html">Pricing</a> | <a href="/contact.html">Contact</a>
        </div>
        <div>
          <h3>Legal</h3>
          <a href="#">Terms of Service</a> | <a href="#">Privacy Policy</a>
        </div>
        <div>
          <h3>Connect</h3>
          <div>
            <a href="#">Facebook</a> | <a href="#">Twitter</a> | <a href="#">Instagram</a>
          </div>
        </div>
        <div style={{ marginTop: '10px' }}>
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Streamer;
