import React, { useState, useRef, useEffect } from 'react';
import "./styles.css";

const Streamer = () => {
  const [roomId, setRoomId] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [comments, setComments] = useState([]); // To store comments
  const [localStream, setLocalStream] = useState(null); // Local video stream for preview
  const ws = useRef(null);
  const mediaRecorder = useRef(null);
  const videoRef = useRef(null); // To display local stream preview

  const startStream = () => {
    if (!roomId) {
      alert('Enter a room ID!');
      return;
    }

    const status = document.getElementById('status');
    ws.current = new WebSocket('wss://final-5sjc.onrender.com'); // Change to 'wss://' for secure WebSocket

    ws.current.onopen = () => {
      status.textContent = 'Connected to server as streamer';
      ws.current.send(JSON.stringify({ type: 'streamer', roomId }));
      setIsStreaming(true);
    };

    ws.current.onclose = () => {
      status.textContent = 'Disconnected from server';
      setIsStreaming(false);
    };

    ws.current.onerror = (err) => {
      console.error('WebSocket error:', err);
      status.textContent = 'WebSocket error occurred';
      setIsStreaming(false);
    };

    // Listen for comments from viewers
    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'comment') {
        setComments((prevComments) => [...prevComments, data]);
      }
    };
  };

  const startSharing = async (getStreamFn, label) => {
    const status = document.getElementById('status');
    try {
      const mediaStream = await getStreamFn();
      status.textContent = `${label} started...`;

      // Set up local stream for preview
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      const videoTrack = mediaStream.getVideoTracks()[0];
      mediaRecorder.current = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm; codecs=vp8',
      });

      mediaRecorder.current.ondataavailable = (event) => {
        if (event.data.size > 0 && ws.current?.readyState === WebSocket.OPEN) {
          ws.current.send(event.data);
        }
      };

      mediaRecorder.current.start(100); // Send chunks every 100ms

      videoTrack.onended = () => {
        mediaRecorder.current?.stop();
        status.textContent = `${label} ended`;
      };

      setLocalStream(mediaStream); // Set local stream for later use
    } catch (err) {
      console.error(`Error starting ${label.toLowerCase()}:`, err);
      status.textContent = `Failed to start ${label.toLowerCase()}`;
    }
  };

  const startScreenSharing = () => {
    startSharing(() => navigator.mediaDevices.getDisplayMedia({ video: true }), 'Screen sharing');
  };

  const startCameraSharing = () => {
    startSharing(() => navigator.mediaDevices.getUserMedia({ video: true }), 'Camera sharing');
  };

  const sendComment = (message) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({
        type: 'comment',
        roomId,
        viewerId: 'Viewer1', // Replace with dynamic viewer ID logic
        message,
      }));
    }
  };

  useEffect(() => {
    return () => {
      // Cleanup on component unmount
      mediaRecorder.current?.stop();
      ws.current?.close();
      if (localStream) {
        localStream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [localStream]);

  return (
    <div>
      <header>
        <nav className="navbar">
          <div className="logo">
            STREAM<span>VIBE</span>
          </div>
          <div className="nav-links">
            <a href=" ">Home</a>
            <a href="about">About</a>
            <a href="viewer">Streams</a>
            <a href="contact">Contact</a>
          </div>
        </nav>
      </header>

      <input
        type="text"
        placeholder="Enter Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
      />
      <button onClick={startStream}>Start Streaming</button>

      {isStreaming && (
        <div>
          <button onClick={startScreenSharing}>Start Screen Share</button>
          <button onClick={startCameraSharing}>Start Camera Share</button>

          {/* Local Stream Preview */}
          <div>
            <video ref={videoRef} autoPlay muted width="300" />
          </div>
        </div>
      )}

      <div>
        {/* Display comments */}
        <h3>Comments</h3>
        <div>
          {comments.map((comment, index) => (
            <p key={index}><strong>{comment.viewerId}: </strong>{comment.message}</p>
          ))}
        </div>
      </div>

      <div>
        {/* Send comment */}
        <input
          type="text"
          placeholder="Enter comment"
          onKeyDown={(e) => {
            if (e.key === 'Enter' && e.target.value.trim()) {
              sendComment(e.target.value);
              e.target.value = ''; // Clear input after sending comment
            }
          }}
        />
      </div>

      <p id="status"></p>

      <footer>
        <div className="footer-content">
          <div className="footer-section">
            <h3>Quick Links</h3>
            <a href=" ">Home</a>
            <a href="/about">About Us</a>
            <a href="/rules">Rules</a>
            <a href="/legacy">Legacy</a>
            <a href="/pricing">Pricing</a>
            <a href="/contact">Contact</a>
          </div>
          <div className="footer-section">
            <h3>Legal</h3>
            <a href="#">Terms of Service</a>
            <a href="#">Privacy Policy</a>
          </div>
          <div className="footer-section">
            <h3>Connect</h3>
            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2024 STREAMVIBE. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Streamer;
